
public class ForLoopInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// print msg 10 time
		
		// print even and odd number 1-100
		boolean flg=true;
		for(int i=1;i<=10;i++) {
			if(i%2==0) {
				System.out.print(i+ "is even number ");	
			System.out.print(i);
		} else {
				System.out.print(i+ "is odd number ");
			}
		}
		
	}

	}

