
public class LearnvariableIn2 {

	static int test;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.print(test);
		
		byte b=127;    // 1 byte
		System.out.print(b);
		
		short s=30;    // 2byte
		System.out.print(s);
		
		int i=40;      // 4 byte
		System.out.print(i);
		
		long l=50;     //8 byte
		System.out.print(l);
		
		float f=20.30f;
		System.out.print(f);
		double d=30.40d;
		System.out.print(d);
		
		char c='A';    //1 byte
		System.out.print(c);
		
		boolean flag=true;
		
		System.out.print(flag);
		
		
		
	}

}
